import{_ as n}from"./plugin-vue_export-helper-c27b6911.js";import{r as s,o as C,c as r,a as o,b as e,d as t,w as d,e as a}from"./app-8cff1e7d.js";const l={},i=a('<h1 id="什么是c语言与我的强烈建议-先学c-而不是c-java等" tabindex="-1"><a class="header-anchor" href="#什么是c语言与我的强烈建议-先学c-而不是c-java等" aria-hidden="true">#</a> 什么是C语言与我的强烈建议 - 先学C,而不是c++,java等</h1><p>C语言除了能让你了解编程的相关概念，带你走进编程的大门，还能让你明白程序的运行原理，比如，计算机的各个部件是如何交互的，程序在内存中是一种怎样的状态，操作系统和用户程序之间有着怎样的“爱恨情仇”，这些底层知识决定了你的发展高度，也决定了你的职业生涯。C语言没学好，后面学<code>数据结构</code>，<code>操作系统</code>，<code>体系结果</code>以及<code>计算机网络</code>等等，都会很累，因为这些都是在与计算机底层打交道，我们的操作系统 <code>windows</code> 和 <code>linux</code> 都是用C语言开发的。</p><p>如果你希望成为出类拔萃的人才，而不仅仅是码农，这么这些知识就是不可逾越的。也只有学习C语言，才能更好地了解它们。有了足够的基础，以后学习其他语言，会触类旁通，很快上手。</p>',3),_=a("<p>软件行业的很多细分学科都是基于C语言的，学习<code>数据结构</code>、<code>算法</code>、<code>操作系统</code>、<code>编译原理</code>等都离不开C语言，所以大学将C语言作为一门公共课程，计算机相关专业（物联网，软件工程，计算机科学与技术等）的同学都要学习。</p><p>C语言被誉为“<code>上帝语言</code>”，它不但奠定了软件产业的基础，还创造了很多其它语言，例如：</p><ul><li>PHP、Python 等都是用C语言开发出来的，虽然平时做项目的时候看不到C语言的影子，但是如果想深入学习 PHP 和 Python，那就要有C语言基础了。</li><li>C++ 和 Objective-C 干脆在C语言的基础上直接进行扩展，增加一些新功能后变成了新的语言，所以学习 C++ 和 Objective-C 之前也要先学习C语言。</li></ul>",3);function p(h,u){const c=s("font");return C(),r("div",null,[i,o("p",null,[e("C语言概念少，词汇少，包含了基本的编程元素，后来的很多语言（C++、Java等）都参考了C语言，说"),t(c,{color:"orange"},{default:d(()=>[e("C语言是现代编程语言的开山鼻祖")]),_:1}),e("毫不夸张，它改变了编程世界。")]),_,o("p",null,[o("strong",null,[t(c,{color:"red"},{default:d(()=>[e("C语言是有史以来最为重要的编程语言，是计算机产业的核心语言：要进入编程行业高手级别必学C语言，要挣大钱必学C语言，要做黑客、红客必学C语言，要面试名企、外企、高薪职位必学C语言。")]),_:1})])])])}const v=n(l,[["render",p],["__file","2-What-is-C-and-my-suggestions.html.vue"]]);export{v as default};
